import { SET_PRODUCTS, SET_FILTER } from "../actions/actions";

const initialState = {
  products: [],
  filter: "" // e.g., "clothing", "electronics"
};

export const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_PRODUCTS:
      return { ...state, products: action.payload };

    case SET_FILTER:
      return { ...state, filter: action.payload };

    default:
      return state;
  }
};
