import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setProducts, setFilter } from "../../redux/actions/actions";
import data from "../../mockData/items.json"; // sample JSON

function Womens() {
  const dispatch = useDispatch();
  const { products, filter } = useSelector((state) => state);

  useEffect(() => {
    // Load products into store
    dispatch(setProducts(data));
  }, [dispatch]);

  // Filtering logic
  const filteredProducts = products.filter((p) =>
    filter ? p.category === filter : true
  );

  return (
    <div>
      <h2>Products</h2>

      <button onClick={() => dispatch(setFilter(""))}>All</button>
      <button onClick={() => dispatch(setFilter("clothing"))}>Clothing</button>
      <button onClick={() => dispatch(setFilter("electronics"))}>Electronics</button>

      <ul>
        {filteredProducts.map((item) => (
          <li key={item.id}>
            {item.name} - {item.category} - â‚¹{item.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Womens
